<body>
  <!-- disable-default-loading -->
  <div class="ntl-container">
    <!-- Info Section -->
    <div class="info-section">
      <div class="info-time">$1</div> <!-- Time -->
      <div class="info-details">
        <div class="info-item"><strong></strong> $2</div>
        <div class="info-item"><strong></strong> $3</div>
        <div class="info-item"><strong>双方关系:</strong> $4</div>
        <div class="info-item"><strong>其他角色:</strong> $5</div>
        <div class="objective-view-container"> <!-- Container for objective view -->
          <strong class="objective-view-title blurred">客观视角:</strong>
          <div class="objective-view-content blurred">$8</div>
        </div>
      </div>
    </div>

    <!-- Flippable Card -->
    <div class="card-container">
      <div class="card" id="ntlCard">
        <!-- Front Face -->
        <div class="card-face card-front">
          <h3>爱情多么美好</h3>
          <p><strong>所见:</strong> $6</p>
          <p><strong>心理:</strong> $7</p>
        </div>
        <!-- Back Face -->
        <div class="card-face card-back">
          <h3>不能说的秘密</h3>
          <p><strong>行为:</strong> $9</p>
          <p><strong>心理:</strong> $10</p>
        </div>
      </div>
    </div>

    <!-- Audio Element -->
    <audio id="backgroundAudio" src="https://files.catbox.moe/caqzho.MP3"></audio>
  </div>

  <script>
    const card = document.getElementById('ntlCard');
    const objectiveViewTitle = document.querySelector('.objective-view-title');
    const objectiveViewContent = document.querySelector('.objective-view-content');
    const audio = document.getElementById('backgroundAudio');

    // Ensure initial state matches CSS (blurred)
    if (!card.classList.contains('is-flipped')) {
      objectiveViewTitle.classList.add('blurred');
      objectiveViewContent.classList.add('blurred');
    }

    card.addEventListener('click', () => {
      card.classList.toggle('is-flipped');
      // Toggle blur on both title and content based on card state
      if (card.classList.contains('is-flipped')) {
        objectiveViewTitle.classList.remove('blurred');
        objectiveViewContent.classList.remove('blurred');
        // Play audio when flipping to back
        try {
          audio.play();
        } catch (error) {
          console.error('Audio playback failed:', error);
        }
      } else {
        objectiveViewTitle.classList.add('blurred');
        objectiveViewContent.classList.add('blurred');
        // Pause audio and reset when flipping to front
        try {
          audio.pause();
          audio.currentTime = 0;
        } catch (error) {
          console.error('Audio pause failed:', error);
        }
      }
    });
  </script>
</body>
</html>
